import React, { Component } from "react"
import axios from 'axios';
class Covid19 extends React.Component {


    constructor() {
        super()
        this.state = {
            lastestDate: '',
            totalCases: '',
            totalDeaths: '',
            totalVaccinated: '',
            changeCases: '',
            changeDeaths: '',
            isLoading: false,
            error: null,
            hits: []


        };
        this.fetchData = this.fetchData.bind(this);

    }



    componentWillMount () {
        this.setState.changeDeaths='2000';

        this.fetchData()
    }


    fetchData() {
        fetch('http://api.openweathermap.org/data/2.5/weather?q=Halifax&appid=64021bde9cafb24bbcff4d7093709eb9')
        .then(response => {
          if(response.ok) return response.json();
          throw new Error('Request failed.');
        })
        .then(data => {
          this.setState(({changeDeaths:parseInt(data['main']['temp'])-273}));
        })
        .catch(error => {
          console.log(error);
        });

    }

    render() {
        return (
            <div className="covid19-container">

                <p><b>Temperature in Halifax: </b>{this.state.changeDeaths}</p>
                {/* <button onClick={() => this.fetchData()}> Fetch Data </button> */}
            </div>
        )
    }
}

export default Covid19;

