import React, { Component } from "react"
import axios from 'axios';
class Covid19 extends React.Component {


    constructor() {
        super()
        this.state = {
            lastestDate: '',
            totalCases: '',
            totalDeaths: '',
            totalVaccinated: '',
            changeCases: '',
            changeDeaths: '',
            isLoading: false,
            error: null,
            hits: []


        };
        this.fetchData = this.fetchData.bind(this);

    }



    componentWillMount () {
        this.setState.changeDeaths='2000';

        this.fetchData()
    }


    fetchData() {
        fetch('http://api.openweathermap.org/data/2.5/weather?q=Halifax&appid=64021bde9cafb24bbcff4d7093709eb9')
        .then(response => {
          if(response.ok) return response.json();
          throw new Error('Request failed.');
        })
        .then(data => {
          this.setState({changeDeaths:data['main']['temp']});
        })
        .catch(error => {
          console.log(error);
        });

    }

    render() {
        return (
            <div className="covid19-container">
                <p><b>Lastest Date: </b>{this.state.hits}</p>
                <p><b>Total Cases: </b>{this.state.isLoading}</p>
                <p><b>Total Deaths: </b>{this.state.totalDeaths}</p>
                <p><b>Total people Vaccinated: </b>{this.state.totalVaccinated}</p>
                <p><b>Change Cases: </b>{this.state.changeCases}</p>
                <p><b>Change Deaths: </b>{this.state.changeDeaths}</p>
                {/* <button onClick={() => this.fetchData()}> Fetch Data </button> */}
            </div>
        )
    }
}

export default Covid19;

